CREATE SCHEMA CineStars;

DROP SCHEMA cinestars;

USE CineStars;

CREATE TABLE pellicola(
id INT PRIMARY KEY AUTO_INCREMENT,
nome VARCHAR(45) UNIQUE NOT NULL,
descrizione VARCHAR(3000) NOT NULL,
tipologia VARCHAR(45) NOT NULL,
id_genere INT NOT NULL REFERENCES genere(id),
anno INT NOT NULL,
paese VARCHAR(45) NOT NULL,
durata INT NOT NULL,
distribuzione VARCHAR(45) NOT NULL,
foto_locandina VARCHAR(3000) NOT NULL,
foto_carosello VARCHAR(3000),
voto INT NOT NULL,
voto_utenti INT,
trailer VARCHAR(3000)
);

DROP TABLE pellicola;




CREATE TABLE artista(
id INT PRIMARY KEY AUTO_INCREMENT,
foto_piccola VARCHAR(3000),
foto_media VARCHAR(3000),
foto_grande VARCHAR(3000),
nome VARCHAR(45) NOT NULL,
cognome VARCHAR(45) NOT NULL,
biografia VARCHAR(3000) NOT NULL,
mansione VARCHAR(45) NOT NULL,
data_nascita DATE NOT NULL,
nazione VARCHAR(45) NOT NULL
);

DROP TABLE artista;



CREATE TABLE genere(
id INT PRIMARY KEY AUTO_INCREMENT,
nome VARCHAR(45) UNIQUE NOT NULL
);

DROP TABLE genere;


CREATE TABLE filmografia(
id INT PRIMARY KEY AUTO_INCREMENT,
id_artista INT NOT NULL REFERENCES artista(id),
id_pellicola INT NOT NULL REFERENCES pellicola(id)
);

DROP TABLE filmografia;













CREATE TABLE recensione(
id INT PRIMARY KEY AUTO_INCREMENT,
descrizione VARCHAR(3000) NOT NULL,
voto INT NOT NULL,
id_utente INT NOT NULL REFERENCES utente(id),
id_pellicola INT NOT NULL REFERENCES pellicola(id),
data_recensione DATE NOT NULL,
UNIQUE(id_utente,id_pellicola)
);

DROP TABLE recensione;




CREATE TABLE utente(
id INT PRIMARY KEY AUTO_INCREMENT,
nome VARCHAR(45) NOT NULL,
cognome VARCHAR(45) NOT NULL,
nazionalita VARCHAR(45) NOT NULL,
username VARCHAR(45) UNIQUE NOT NULL,
password VARCHAR(45) NOT NULL
);

DROP TABLE utente;



CREATE TABLE watchlist(
	id INT PRIMARY KEY AUTO_INCREMENT,
	id_utente INT NOT NULL REFERENCES utente(id),
	id_pellicola INT NOT NULL REFERENCES pellicola(id),
	UNIQUE (id_utente,id_pellicola)
);

